import React, { Component } from 'react'
import {connect} from 'react-redux'
import ContainerStyle from './ContainerStyle.css'

class CalculatorContainer extends Component {

    constructor(props){
        super(props);
    }

    render(){
        /* Returns math operation result from redux store*/
        const result = this.props.storeCounter

        return(
            <div className="container">
                <div className="row col-result">
                    <div className="col-12 ">{result}</div>
                </div>
                <div className="row row-color">
                    <div className="col col-color">AC</div>
                    <div className="col col-color">/</div>
                    <div className="col col-color">X</div>
                </div>
                <div className="row row-color">
                    <div className="col col-color">7</div>
                    <div className="col col-color">8</div>
                    <div className="col col-color">9</div>
                    <div className="col col-color">-</div>
                </div>
                <div className="row row-color">
                    <div className="col col-color">4</div>
                    <div className="col col-color">5</div>
                    <div className="col col-color">6</div>
                    <div className="col col-color">+</div>
                </div>
                <div className="row row-color">
                    <div className="col col-color">1</div>
                    <div className="col col-color">2</div>
                    <div className="col col-color">3</div>
                    <div className="col col-color">=</div>
                </div>
                <div className="row row-color">
                    <div className="col-6 col-color">0</div>
                    <div className="col-3 col-color">.</div>
                    <div className='col-3 col-color'></div>  
                </div>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return{
        storeCounter:state.count
    }
}
 /* anonymous ascynch function dispatching the action creator
 to the redux store reducer */
const mapDispatchToProps = (dispatch) => {
    return{
        addOne:() => {dispatch({type:'ADD'})}
    }
}

export default connect(mapStateToProps,mapDispatchToProps)(CalculatorContainer)
