/* Action */
const ADD = 'ADD'
const RESET = 'RESET'
const DIVISION = 'DIVISION'
const MULTIPLY = 'MULTIPLY'

const defaultState = {
    total:0
}

const reducer = (state = defaultState,action) => {

    switch(action.type){
        case ADD:
            return{
                total:state.total+1
            }
        case RESET:
            return{
                total:state.total = 0
            }
        case DIVISION:
            return{
                total:state.total
            }
        case MULTIPLY:
            return{
                total:state.total
            }
        default:
            return state
    }
}

export default reducer;
