/* Action */
const ADDONE = 'ADDONE'
const ADDTWO = 'ADDTWO'
const RESET = 'RESET'
const DIVISION = 'DIVISION'
const MULTIPLY = 'MULTIPLY'
const EQUAL = 'EQUAL'
const DECIMAL = 'DECIMAL'
/* check for zero */
const notDivisible = 0

/* default reducer state */
const defaultState = {
    total:0,
    numberArray:[0,0,0]
}

const one = [1];

const reducer = (state = defaultState,action) => {

    switch(action.type){
        case ADDONE:
            return{
                ...state,
                numberArray:state.numberArray.concat(one)
            }
        default:
            return state
    }

}

export default reducer;
