/* Action */
const ADD = 'ADD'
const RESET = 'RESET'

const defaultState = {
    count:0
}

const reducer = (state = defaultState,action) => {

    switch(action.type){
        case ADD:
            return{
                count:state.count+1
            }
        case RESET:{
            count:state.count = 0
        }
        default:
            return state
    }
}

export default reducer;
