/* Action */
const ADDONE = 'ADDONE'
const ADDTWO = 'ADDTWO'
const RESET = 'RESET'
const DIVISION = 'DIVISION'
const MULTIPLY = 'MULTIPLY'
const EQUAL = 'EQUAL'
const DECIMAL = 'DECIMAL'
/* check for zero */
const notDivisible = 0

/* default reducer state */
const defaultState = {
    total:0,
    numberArray:[0,0,0]
}



const reducer = (state = defaultState,action) => {

    if(EQUAL == action.type){
        const copyObj = Object.assign(state)
        copyObj.numberArray.push(1)
        console.log(copyObj.numberArray)
    }

    return{
        ...state
    }
}

export default reducer;
