/* Action */
const ADDONE = 'ADDONE'
const ADDTWO = 'ADDTWO'
const RESET = 'RESET'
const DIVISION = 'DIVISION'
const MULTIPLY = 'MULTIPLY'
const EQUAL = 'EQUAL'
const DECIMAL = 'DECIMAL'
/* check for zero */
const notDivisible = 0

/* default reducer state */
const defaultState = {
    total:0,
    numberArray:[0]
}



const reducer = (state = defaultState,action) => {

    if(EQUAL == action.type){
        console.log('equls')
    }

    switch(action.type){
        case ADDONE:
            if(state.numberArray[0] === 0){
                return{...state,numberArray:state.numberArray[0] = 1}
            }
            else{
                state.numberArray.push(1)
            }
            break;
        case ADDTWO:
            if(state.numberArray[0] === 0){
                state.numberArray[0] = 2
            }
            else{
                state.numberArray.push(2)
            }
            break;
    }

    return{
        ...state
    }
}

export default reducer;
