import React, { Component } from 'react'
import {connect} from 'react-redux'
import ContainerStyle from './ContainerStyle.css'

class CalculatorContainer extends Component {

    constructor(props){
        super(props);
    }

    render(){
        /* Returns math operation result from redux store*/
        const result = this.props.storeCounter

        return(
            <div className="container">
                <div className="row col-result-layout">
                <div className="col-12 col-subresult">{result}</div>
                    <div className="col-12 ">{result}</div>
                </div>
                <div className="row">
                    <div className="col"><button onClick = {this.props.reset}>AC</button></div>
                    <div className="col"><button onClick ={this.props.division}>/</button></div>
                    <div className="col">X</div>
                </div>
                <div className="row">
                    <div className="col">7</div>
                    <div className="col">8</div>
                    <div className="col">9</div>
                    <div className="col">-</div>
                </div>
                <div className="row">
                    <div className="col">4</div>
                    <div className="col">5</div>
                    <div className="col">6</div>
                    <div className="col">+</div>
                </div>
                <div className="row">
                    <div className="col"><button onClick = {this.props.addOne}>1</button></div>
                    <div className="col">2</div>
                    <div className="col">3</div>
                    <div className="col">=</div>
                </div>
                <div className="row">
                    <div className="col-6">0</div>
                    <div className="col-3">.</div>
                    <div className='col-3'></div>  
                </div>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return{
        storeCounter:state.total
    }
}
 /* anonymous ascynch function dispatching the action creator
 to the redux store reducer */
const mapDispatchToProps = (dispatch) => {
    return{
        addOne:() => {dispatch({type:'ADD'})},
        reset:() => {dispatch({type:'RESET'})}
    }
}

export default connect(mapStateToProps,mapDispatchToProps)(CalculatorContainer)
