import React, { Component } from 'react'
import {connect} from 'react-redux'
import ContainerStyle from './ContainerStyle.css'
import Display from './Display.js'
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'

class CalculatorContainer extends Component {


    render() {
        const style = {
            backgroundColor:'white'
        }
        return(
            <div style={style} class="container">
                <div class="row row-color">
                    <div class="col col-color">1</div>
                    <div class="col">2</div>
                    <div class="col">3</div>
                    <div class="col">4</div>
                </div>
                <div class="row">
                    <div class="col-8">col-8</div>
                    <div class="col-4">col-4</div>
                </div>
            </div>

        )
    }
}

const mapStateToProps = (state) => {
    return{
        storeCounter:state.count
    }
}
 /* anonymous ascynch function dispatching the action creator
 to the redux store reducer */
const mapDispatchToProps = (dispatch) => {
    return{
        addOne:() => {dispatch({type:'ADD'})}
    }
}

export default connect(mapStateToProps,mapDispatchToProps)(CalculatorContainer)
